function cleanAgentOutput(agentResponse) {
  // Case 1: If output is already valid JSON array, return it directly
  if (Array.isArray(agentResponse)) {
    return agentResponse;
  }

  // Case 2: If output contains multiple JSON blocks in markdown
  const jsonStrings = [];
  const regex = /```json\n([\s\S]*?)\n```/g;
  let match;

  // Extract all JSON blocks from markdown
  while ((match = regex.exec(agentResponse)) !== null) {
    try {
      const parsed = JSON.parse(match[1]);
      if (Array.isArray(parsed)) {
        jsonStrings.push(parsed);
      }
    } catch (e) {
      console.error("Failed to parse JSON block:", e);
    }
  }

  // Case 3: If we found multiple JSON blocks, take the last valid one
  if (jsonStrings.length > 0) {
    return jsonStrings[jsonStrings.length - 1];
  }

  // Case 4: Try to parse the entire response as JSON
  try {
    const parsed = JSON.parse(agentResponse);
    return Array.isArray(parsed) ? parsed : [];
  } catch (e) {
    console.error("Failed to parse as JSON:", e);
  }

  return [];
}

// Process all outputs from the Agent node
const processedOutputs = $input.all().flatMap(item => {
  const cleaned = cleanAgentOutput(item.json.output || item.json);
  return Array.isArray(cleaned) ? cleaned : [];
});

// Get unique records (in case of duplicates)
const uniqueRecords = [];
const seen = new Set();

processedOutputs.forEach(record => {
  const key = `${record["Employee ID"]}-${record["Date"]}`;
  if (!seen.has(key)) {
    seen.add(key);
    uniqueRecords.push(record);
  }
});

// Return in n8n format
return uniqueRecords.map(record => ({ json: record }));

